             write(16,*) 'TYPE CODE: HARM  ***************'
             write(16,*) 'ELECTRIC FIELD (Fourier series expansion)'
             call rharm
             write(16,*) '********************************'
           ELSEIF(I.eq.16) then
!  After BUNCHER: buncher as a single element
