             write(16,*) 'TYPE CODE: RFQCL DEPRECATED ****'
             write(16,*) 'Use RFQPTQ instead *************'
             write(ierr,*)  
             write(ierr,*)'TYPE CODE: RFQCL DEPRECATED ****'
             write(ierr,*)'Use RFQPTQ instead *************'
             STOP
