             READ(IN,*) BEAMC,sce10
             beame=beamc
             if(.not.xiset) then
               kt3h=kt3h+1
               write(tif,7001) BEAMC
7001           format(' XI= ',f9.4)
               trace3h(kt3h)=tif
               xiset=.true.
