!! SUBROUTINE fielde(lc,mc,nc,isucc)
!! beam self-fields computation called by HERSC
!! look for the parity of the currents lc,mc and nc
!! compute the corresponding field components
!!     isucc=1: a<b,c
!!     isucc=2: b<a,c
!!     isucc=3: c<a,b
!! x,y,z are the scaling coordinates: x/a,y/b,z/c
!! The corresponding analytical equations are in the number of the
!! tables. The table number in 'comments' refers to the corresponding
!! analytical equations
!< *******************************************************************
       SUBROUTINE fielde(lc,mc,nc,isucc)
       implicit real(8) (a-h,o-z)
       common/rms/rms(3,50),s1,s2,s3
       common/partcl/x,y,z
       common/const/pi2,sqpi,pwtpi,sqpi2,sq2pi
       common/field/ex,ey,ez
       common/randu/ck(15),kmax
!           pi2=pi*pi, sqpi=(pi/2)**3/2
       common/cars3/exs3,exs2,exs1,as3,sgns3,s32,pwas3,pw3as3
!         exs3=exp(-s3*s3/2),abs3=abs(s3),sgns3=sign s3,s32=s3*s3
!         pwas3=as3**(2(it1+it2+it3)+1),pw3as3=as3**(2*it3))
       common/sgpth/mksgi,mksgp
       common/ftth/makti,maktp
       common/fsth/maksi,maksp
       logical maksi,maksp
       logical makti,maktp
       logical mksgi,mksgp
       logical lpl,lpm,lpn
!         look for the parity of the currents lc,mc,nc
       lpl=.false.
       lpm=.false.
       lpn=.false.
       makti=.false.
       maktp=.false.
       maksi=.false.
       maksp=.false.
       mksgi=.false.
       mksgp=.false.
       xlc=lc
       tl=xlc-2.*int(xlc/2.+0.0001)
       fprec=epsilon(tl)
!       if(tl.eq.0.)lpl=.true.
       if(abs(tl).le.fprec)lpl=.true.
       xmc=mc
       tm=xmc-2.*int(xmc/2.+0.0001)
!       if(tm.eq.0.)lpm=.true.
       if(abs(tm).le.fprec)lpm=.true.
       xnc=nc
       tn=xnc-2.*int(xnc/2.+0.0001)
!       if(tn.eq.0.)lpn=.true.
       if(abs(tn).le.fprec)lpn=.true.
       if(lpl.and.lpm.and.lpn) itpar=1
       if(lpl.and.lpm.and..not.lpn) itpar=2
       if(lpl.and..not.lpm.and.lpn) itpar=3
       if(lpl.and..not.lpm.and..not.lpn) itpar=4
       if(.not.lpl.and.lpm.and.lpn) itpar=5
       if(.not.lpl.and.lpm.and..not.lpn) itpar=6
       if(.not.lpl.and..not.lpm.and.lpn) itpar=7
       if(.not.lpl.and..not.lpm.and..not.lpn) itpar=8
!
!  values of the variables it1,it2,it3 in relation with lc,mc,nc
!  the initial values of these lc,mc,nc is zero
       if (isucc.eq.1) then
!  a<b,c
         if(itpar.eq.1) then
!       E E E and  a<b,c:
!                  table 67-a: Ex=E(2t3+1,2t2,2t1)
!                              Ey=E(2t3,2t2+1,2t1)
!                              Ez=E(2t3,2t2,2t1+1)
!        values of it1,it2,it3 in table 3-a
           it3=lc/2
           it2=mc/2
           it1=nc/2
!           if(s3.ne.0.) then
           if(abs(s3).gt.fprec) then
!            it=2*(it1+it2+it3)+1
             it=lc+mc+nc+1
             pwas3=as3**it
           endif
           ex=eipp(it1,it2,it3)
           ey=epip(it1,it2,it3)
           ez=eppi(it1,it2,it3)
           return
         endif
         if(itpar.eq.2) then
!       E E I and  a<b,c
!                  table 67-g: Ex=E(2t3+1,2t2,2t1+1)
!                              Ey=E(2t3,2t2+1,2t1+1)
!                              Ez=E(2t3,2t2,2t1)
!        values of it1,it2,it3 in table 3-g
!        x-direction and y-direction
           it3=lc/2
           it2=mc/2
           it1=(nc-1)/2
!           if(s3.ne.0.) then
           if(abs(s3).gt.fprec) then
!     it=2*(it1+it2+it3)+1
             it=lc+mc+nc
             pwas3=as3**it
           endif
           ex=eipi(it1,it2,it3)
           ey=epii(it1,it2,it3)
!     z-direction
           it1=(nc+1)/2
!           if(s3.ne.0.)pwas3=pwas3*s32
           if(abs(s3).gt.fprec)pwas3=pwas3*s32
!     it=2*(it1+it2+it3)+1
           ez=eppp(it1,it2,it3)
           return
         endif
         if(itpar.eq.3) then
!       E I E and  a<b,c
!               in table 67-h: Ex=E(2t3+1,2t2+1,2t1)
!                              Ey=E(2t3,2t2,2t1)
!                              Ez=E(2t3,2t2+1,2t1+1)
!        values of it1,it2,it3 in table 3-h
!        x-direction
           it3=lc/2
           it2=(mc-1)/2
           it1=nc/2
!           if(s3.ne.0.) then
           if(abs(s3).gt.fprec) then
!     it=2*(it1+it2+it3)+1
             it=lc+mc+nc
             apwas3=as3**it
             pwas3=apwas3
           endif
           ex=eiip(it1,it2,it3)
!        y-direction
           it3=lc/2
           it2=(mc+1)/2
           it1=nc/2
!       it=2*(it1+it2+it3)+1
!           if(s3.ne.0.) pwas3=pwas3*s32
           if(abs(s3).gt.fprec)pwas3=pwas3*s32           
           ey=eppp(it1,it2,it3)
!        z-direction
           it3=lc/2
           it2=(mc-1)/2
           it1=nc/2
!           if(s3.ne.0.)pwas3=apwas3
           if(abs(s3).gt.fprec)pwas3=apwas3           
!       it=2*(it1+it2+it3)+1
           ez=epii(it1,it2,it3)
           return
         endif
         if(itpar.eq.4) then
!       E I I and  a<b,c
!               in table 67-f: Ex=E(2t3+1,2t2+1,2t1+1)
!                              Ey=E(2t3,2t2,2t1+1)
!                              Ez=E(2t3,2t2+1,2t1)
!        values of it1,it2,it3 in table 3-f
!        x-direction
           it3=lc/2
           it2=(mc-1)/2
           it1=(nc-1)/2
!           if(s3.ne.0.) then
           if(abs(s3).gt.fprec) then
!    it=2*(it1+it2+it3)+1
             it=lc+mc+nc-1
             pwas3=as3**it
           endif
           ex=eiii(it1,it2,it3)
!        y-direction
           it3=lc/2
           it2=(mc+1)/2
           it1=(nc-1)/2
!         it=2*(it1+it2+it3)+1
!           if(s3.ne.0.)pwas3=pwas3*s32
           if(abs(s3).gt.fprec)pwas3=pwas3*s32           
           ey=eppi(it1,it2,it3)
!        z-direction
           it3=lc/2
           it2=(mc-1)/2
           it1=(nc+1)/2
           ez=epip(it1,it2,it3)
           return
         endif
         if(itpar.eq.5) then
!       I E E and  a<b,c
!               in table 67-c: Ex=E(2t3,2t2,2t1)
!                              Ey=E(2t3+1,2t2+1,2t1)
!                              Ez=E(2t3+1,2t2,2t1+1)
!        values of it1,it2,it3 in table 3-c
!        x-direction
           it3=(lc+1)/2
           it2=mc/2
           it1=nc/2
!           if(s3.ne.0.) then
           if(abs(s3).gt.fprec) then
!       it=2*(it1+it2+it3)+1
             it=lc+mc+nc
             apwas3=as3**it
             pwas3=apwas3*s32
           endif
           ex=eppp(it1,it2,it3)
!        y-direction
           it3=(lc-1)/2
           it2=mc/2
           it1=nc/2
!           if(s3.ne.0.) then
           if(abs(s3).gt.fprec) then
!       it=2*(it1+it2+it3)+1
             pwas3=apwas3
             pw3as3=apwas3
           endif
           ey=eiip(it1,it2,it3)
!        z-direction
           it3=(lc-1)/2
           it2=mc/2
           it1=nc/2
           ez=eipi(it1,it2,it3)
           return
         endif
         if(itpar.eq.6) then
!       I E I and  a<b,c
!               in table 67-e: Ex=E(2t3,2t2,2t1+1)
!                              Ey=E(2t3+1,2t2+1,2t1+1)
!                              Ez=E(2t3+1,2t2,2t1)
!        values of it1,it2,it3 in table 3-e
!        x-direction
           it3=(lc+1)/2
           it2=mc/2
           it1=(nc-1)/2
!           if(s3.ne.0.) then
           if(abs(s3).gt.fprec) then
