           it1=nc/2
!         it=2*(it1+it2+it3)+1
!           if(s3.ne.0.) pwas3=apwas3
           if(abs(s3).gt.fprec) pwas3=apwas3
           ez=eiii(it1,it2,it3)
           return
         endif
         if(itpar.eq.8) then
!       I I I and  a<b,c
!               in table 67-b: Ex=E(2t3,2t2+1,2t1+1)
!                              Ey=E(2t3+1,2t2,2t1+1)
!                              Ez=E(2t3+1,2t2+1,2t1)
!        values of it1,it2,it3 in table 3-b
