       common/ftth/makti,maktp
       logical makti,maktp
       tipp=0.
       if(.not.makti) then
         makti=.true.
         kk=1
         do k=2,kmax,2
           km1=k-1
           tt1=0.
           jm1=0
           xsj1=1.
           jj1=1
           do j1=1,km1,2
