!! SUBROUTINE plprf2
!! Store header and envelopes in binary file for
!! graphics post-processor
!< *******************************************************************
       SUBROUTINE plprf2
       implicit real(8) (a-h,o-z)
       dimension xx(6001),yy(6001)
       CHARACTER CAR*80,text*80
       common/prof/car
       COMMON/DPLT/ZDEB,ZFIN,YWMAX,YPMAX,RMSN
       COMMON/PLTPRF/SPRFY(6001),SPRFZ(6001),SPRFL(6001),SPRFW(6001),
     X               SPRFP(6001),SPRNG(6001),PRLAB(6001),IPRF
       CHARACTER*(8) PRLAB
       IDEB=1
       IFIN=IPRF
       DO I=2,IPRF
         IF((ZDEB.GT.SPRFL(I-1)).AND.(ZDEB.LE.SPRFL(I)))IDEB=I
         IF((ZFIN.GE.SPRFL(I-1)).AND.(ZFIN.LT.SPRFL(I)))IFIN=I-1
       ENDDO
       xxmax=ywmax/1000.
       xymax=ypmax
       if (xxmax.le.0.) then
         XXMAX=0.
         DO I=IDEB,IFIN
           IF(0.5*SPRFW(I)*RMSN.GT.XXMAX) XXMAX=0.5*SPRFW(I)*RMSN
         ENDDO
       endif
       if (xymax.le.0.) then
         XYMAX=0.
         DO I=IDEB,IFIN
           IF(0.5*SPRFP(I)*RMSN.GT.XYMAX) XYMAX=0.5*SPRFP(I)*RMSN
         ENDDO
       endif
! Store header and envelopes in binary file for
! graphics post-processor
!
! igrtyp is type of graph (igrtyp=4 for dW/W envelope plots)
       igrtyp=4
       text='dW/W envelope       '
       text(21:80)=car(1:60)
       WRITE(66,*) igrtyp
       WRITE(66,*) text
       xx(1)=zdeb
       xx(2)=zfin
       yy(1)=0.
       yy(2)=xxmax*1000.
       WRITE(66,*) xx(1),xx(2),yy(1),yy(2)
       icnt=0
       DO I=IDEB,IFIN
         icnt=icnt+1
         xx(icnt)=sprfl(i)
         yy(icnt)=0.5*sprfw(i)*1000.*RMSN
       ENDDO
! write envelope coordinates to graphics file
       WRITE(66,*) icnt
       DO I=1,icnt
         WRITE(66,*) xx(i),yy(i)
       ENDDO
! Store header and envelopes in binary file for
! graphics post-processor
!
! igrtyp is type of graph (igrtyp=5 for dPHI envelope plots)
       igrtyp=5
       text='dPHI envelope       '
       text(21:80)=car(1:60)
       WRITE(66,*) igrtyp
       WRITE(66,*) text
       xx(1)=zdeb
       xx(2)=zfin
       yy(1)=0.
       yy(2)=xymax
       write(16,*) ' dW/WMAX dPhiMAX ',xxmax,xymax
       WRITE(66,*) xx(1),xx(2),yy(1),yy(2)
       icnt=0
       DO I=IDEB,IFIN
         icnt=icnt+1
         xx(icnt)=sprfl(i)
         yy(icnt)=0.5*sprfp(i)*RMSN
       ENDDO
! write envelope coordinates to graphics file
       WRITE(66,*) icnt
       DO I=1,icnt
         WRITE(66,*) xx(i),yy(i)
       ENDDO
       IPRF=IPRF+1
       RETURN
       END
!> *******************************************************************
!! FUNCTION slope(N,XV)
!! first derivative of the spline function
!< *******************************************************************
       FUNCTION slope(N,XV)
       implicit real(8) (a-h,o-z)
       common/spl/x(4000),y(4000),s(3000),p(3000),q(3000)
       do i=2,n
         xtvi=xv-x(i)
         if(xtvi.le.0.) EXIT
       enddo
       if(xtvi.ge.0.) then
         I=I-1
         AVX=X(I+1)-X(I)
         SLOPE=S(I+1)*AVX/3.+S(I)*AVX/6.+(Y(I+1)-Y(I))/AVX
       else  
         I=I-1
         DGX=XV-X(I)
         DDX=X(I+1)-XV
         AVX=X(I+1)-X(I)
         SLOPE=-(S(I)*DDX*DDX)/(2.*AVX)+(S(I+1)*DGX*DGX)/(2.*AVX)
     *          +((Y(I+1)-Y(I))/AVX)-(AVX*(S(I+1)-S(I))/6.)
       endif
       return
       end
!> *******************************************************************
!! FUNCTION spline (N,XV)
!! SPLINE FUNCTION
!< *******************************************************************
       FUNCTION spline (N,XV)
       implicit real(8) (a-h,o-z)
       COMMON /SPL/X(4000),Y(4000),S(3000),P(3000),Q(3000)
       spline=y(1)
       xtv1=xv-x(1)
       fprec=epsilon(xtv1)
       if(xtv1.lt.0.) then
         SPLINE=Y(1)+((Y(2)-Y(1))/(X(2)-X(1))-S(2)*(X(2)-X(1))/6.)
     *          *(XV-X(1))
         return
       endif
!       if(xtv1.eq.0.0) then
       if(abs(xtv1).le.fprec) then
         SPLINE=Y(1)
         return
       endif
       if(xtv1.gt.0.) then
         xtvn=xv-x(n)
!         if(xtvn.eq.0.00) then
         if(abs(xtvn).le.fprec) then
           SPLINE=Y(N)
           return
         endif
         if(xtvn.gt.0.) then
           SPLINE=Y(N)+((Y(N)-Y(N-1))/
     *           (X(N)-X(N-1))+S(N-1)*(X(N)-X(N-1))/6.)*(XV-X(N))
           return
         endif
         if(xtvn.lt.0.) then
           do i=2,n
             xtvi=xv-x(i)
             if(xtvi.le.0.) EXIT
           enddo
           if(xtvi.ge.0.) then
             spline=y(i)
           else
             I=I-1
             DGX=XV-X(I)
             DDX=X(I+1)-XV
             AVX=X(I+1)-X(I)
             SPLINE=S(I)*DDX**3/(6.*AVX)+S(I+1)*DGX**3/(6.*AVX)
     *    +(Y(I+1)/AVX-S(I+1)*AVX/6.)*DGX+(Y(I)/AVX-S(I)*AVX/6.)*DDX
           endif
         endif
       endif
       return
       end
!> *******************************************************************
!! SUBROUTINE DERIV2(N)
!! second derivative of spline functions at position (x,y)
!< *******************************************************************
       SUBROUTINE DERIV2(N)
       implicit real(8) (a-h,o-z)
       common/spl/x(4000),y(4000),s(3000),p(3000),q(3000)
       AVXN=X(N)-X(N-1)
       AVVXN=X(N-1)-X(N-2)
       AVYN=Y(N)-Y(N-1)
       AVVYN=Y(N-1)-Y(N-2)
       F=AVXN-(AVVXN**2)/AVXN
       P(N-1)=1.
       Q(N-1)=0.
       fprec=epsilon(f)
!       if(f.ne.0.) then
       if(abs(f).gt.fprec) then
         P(N-1)=(-2.*AVXN-3.*AVVXN-AVVXN**2/AVXN)/F
         Q(N-1)=6.*(AVYN/AVXN-AVVYN/AVVXN)/F
       endif
       NM1=N-1
       DO J=2,NM1
         I=N-J
         AVX=X(I+1)-X(I)
         AVVX=X(I+2)-X(I+1)
         AVY=Y(I+1)-Y(I)
         AVVY=Y(I+2)-Y(I+1)
         D=2.*(AVX+AVVX)+AVVX*P(I+1)
         P(I)=-AVX/D
         Q(I)=(6.*(AVVY/AVVX-AVY/AVX)-AVVX*Q(I+1))/D
       ENDDO
       AVX1=X(2)-X(1)
       AVVX1=X(3)-X(2)
       G1=(AVVX1/AVX1)+1.-P(2)-(Q(2)/Q(1))
       G2=(AVVX1/(AVX1*P(1)))-(AVVX1/AVX1)-1.+P(2)
       S(1)=(Q(1)*G1)/(P(1)*G2)
       DO I=1,NM1
         S(I+1)=P(I)*S(I)+Q(I)
       ENDDO
       RETURN
       END
