!! SUBROUTINE zrotat(zrot)
!! BEAM ROTATION
!!     the transverse coordinates X and Y may be rotated
!!     through an angle about the axis tangent to the
!!     central trajectory at the point in question.
!< *******************************************************************
       SUBROUTINE zrotat(zrot)
       implicit real(8) (a-h,o-z)
       parameter (iptsz=1000002,maxcell1=4004)
       common/faisc/f(10,iptsz),imax,ngood
       common/grot/rzot,izrot
       COMMON /CONSTA/ VL, PI, XMAT, RPEL, QST
       COMMON/DAVCOM/dav1(maxcell1,40),davtot,iitem(maxcell1),idav
       logical izrot
       DIMENSION  RS(6,6),FF(6),FC(6)
       WRITE(16,100) ZROT
 100   FORMAT(/,20X,'BEAM ROTATION ',F10.4
     1 ,'  degrees  ABOUT THE POSITIVE Z-AXIS',/)
       if(.not.izrot) then
         izrot=.true.
         rzot=zrot
       else
         izrot=.false.
       endif
!  Daves start
       idav=idav+1
       iitem(idav)=20
       dav1(idav,1)=zrot
! initialize
       DO IA=1,4
         DO IB=1,4
           RS(IA,IB)=0.
         ENDDO
       ENDDO
!      Conversion DEG ==> RAD
       ZROT=  ZROT*pi/180.
       RS44  =  COS(ZROT)
       RS( 4,4 )  =    RS44
       RS( 3,3 )  =    RS44
       RS( 2,2 )  =    RS44
       RS( 1,1 )  =    RS44
       RS24     =  SIN(ZROT)
       RS( 2,4 )  =    RS24
       RS( 1,3 )  =    RS24
       RS( 4,2 )  =  - RS24
       RS( 3,1 )  =  - RS24
       DO II=1,ngood
         FF(1)=F(2,II)
         FF(2)=F(3,II)
         FF(3)=F(4,II)
         FF(4)=F(5,II)
         DO IA=1,4
           FC(IA)=0.
           DO IB=1,4
             FC(IA)=FC(IA)+FF(IB)*RS(IA,IB)
           ENDDO
         ENDDO
         F(2,II)=FC(1)
         F(3,II)=FC(2)
         F(4,II)=FC(3)
         F(5,II)=FC(4)
       ENDDO
       RETURN
       END
