       SUBROUTINE buncnum
       implicit real(8) (a-h,o-z)
       parameter (iptsz=1000002,maxcell=3000,maxcell1=4004)
       COMMON/TTFS/DYNT(MAXCELL),DYNTP(MAXCELL),DYNTPP(MAXCELL),
     *   DYNE0(MAXCELL),DYNPH(MAXCELL),DYNLG(MAXCELL),FHPAR,NC
       COMMON/POSI/IST
