         xspl1=-fhi*sin(fhi*t1+phzi)*tspl1/bgt1
         xspl2=-fhi*sin(fhi*t2+phzi)*tspl2/bgt2
         xspl3=-fhi*sin(fhi*t3+phzi)*tspl3/bgt3
