!> *******************************************************************
!! FUNCTION spta0
!! Special Transit time factor t(k) (single cell)
!< *******************************************************************
       FUNCTION spta0(xkitr,nrc)
       implicit real(8) (a-h,o-z)
       common/consta/vl,pi,xmat,rpel,qst
       common/cell/yf(10000),xf(10000),xlim(15),flength,fhc,att,
     *             npoint(15),ncell
       common/gaus13/H(13),T(13)
       common/gaus17/H1(17),T1(17)
       common/fcv/jsav
! ---       xk=fhc*2.*pi/(betr*vl)
       ar=0.
       xc1=xlim(nrc)
       xc2=xlim(nrc+1)
       DO I=1,17
         XC= (XC2+XC1)/2.+(XC2-XC1)*H1(I)/2.
         rfonc=fcav(xc,nrc)
         AR=AR+T1(I)*RFONC*COS(XKitr*XC)
       enddo
       spta0=ar*(xc2-xc1)
       return
       END
!> *******************************************************************
!! FUNCTION sptta0(xkitr)
!! Special Transit time factor t(k) (multi-cells)
!< *******************************************************************
       FUNCTION sptta0(xkitr)
       implicit real(8) (a-h,o-z)
       common/cell/yf(10000),xf(10000),xlim(15),flength,fhc,att,
     *             npoint(15),ncell
       common/consta/vl,pi,xmat,rpel,qst
       COMMON/FUNC/A(200),YLG,ATTE,NCEL,NHARM
       COMMON/TTFC/TK,T1K,T2K,SK,S1K,S2K,FH
       common/rfield/ifield
       common/gaus13/H(13),T(13)
       dimension xmin(15)
       logical ifield
       fprec=epsilon(xmin(1))
       if(ifield) then
         sptta0=0.
         do i=1,ncell
           sptta0=sptta0+spta0(xkitr,i)
         enddo
        return
       else
! -----         xk=fh/(betr*vl)
         xmin(1)=0.
         do i=2,ncel+2
           xmin(i)=0.
         enddo
         do i=2,ncel+1
           xmin(i)=ylg*(i-1)/ncel
         enddo
         ipas=1
         sptta0=0.
         ar=0.
         xc1=0.
         xc2=0.
         DO
!           if(xmin(ipas+1).eq.0.or.ipas.gt.(ncel+2)) EXIT
           if(abs(xmin(ipas+1)).le.fprec .or. ipas.gt.(ncel+2)) EXIT
           do i=1,13
             xc1=xmin(ipas)
             xc2=xmin(ipas+1)
             XC= (XC2+XC1)/2.+(XC2-XC1)*T(I)/2.
             RFONC= FONE(XC)
             AR=AR+H(I)*RFONC*COS(XKITR*XC)
           enddo
           AR=AR*(XC2-XC1)
           SPTTA0=SPTTA0+AR
           AR=0.
           IPAS=IPAS+1
         ENDDO
       endif
       return
       end
!> *******************************************************************
!! FUNCTION spsb0(xk,nrc)
!! Special Transit time factor s(k) (single cell)
!< *******************************************************************
       FUNCTION spsb0(xkitr,nrc)
       implicit real(8) (a-h,o-z)
       common/cell/yf(10000),xf(10000),xlim(15),flength,fhc,att,
     *             npoint(15),ncell
       common/consta/vl,pi,xmat,rpel,qst
       common/gaus13/H(13),T(13)
       common/gaus17/H1(17),T1(17)
! ---       xk=fhc*2.*pi/(betr*vl)
       xc1=xlim(nrc)
       xc2=xlim(nrc+1)
       br=0.
       DO I=1,17
         XC= (XC2+XC1)/2.+(XC2-XC1)*H1(I)/2.
         rfonc=fcav(xc,nrc)
         BR=BR+T1(I)*RFONC*SIN(XKITR*XC)
       enddo
       SPSB0=BR*(XC2-XC1)
       return
       END
!> *******************************************************************
!! FUNCTION sptsb0(xkitr)
!! Special Transit time factor s(k) (multi-cells)
!< *******************************************************************
       FUNCTION sptsb0(xkitr)
       implicit real(8) (a-h,o-z)
       common/cell/yf(10000),xf(10000),xlim(15),flength,fhc,att,
     *             npoint(15),ncell
       common/consta/vl,pi,xmat,rpel,qst
       COMMON/FUNC/A(200),YLG,ATTE,NCEL,NHARM
       COMMON/TTFC/TK,T1K,T2K,SK,S1K,S2K,FH
       common/rfield/ifield
       common/gaus13/H(13),T(13)
       dimension xmin(15)
       logical ifield
       fprec=epsilon(xmin(1))
       if(ifield) then
         sptsb0=0.
         do i=1,ncell
           sptsb0=sptsb0+spsb0(xkitr,i)
         enddo
         return
       else
! ---       xk=fh/(betr*vl)
         xmin(1)=0.
         do i=2,ncel+2
           xmin(i)=0.
         enddo
         do i=2,ncel+1
           xmin(i)=ylg*(i-1)/ncel
         enddo
         ipas=1
         sptsb0=0.
         br=0.
         xc1=0.
         xc2=0.
         DO
!           if(xmin(ipas+1).eq.0.or.ipas.gt.(ncel+2)) EXIT
           if(abs(xmin(ipas+1)).le.fprec .or. ipas.gt.(ncel+2)) EXIT
           do i=1,13
             xc1=xmin(ipas)
             xc2=xmin(ipas+1)
             XC= (XC2+XC1)/2.+(XC2-XC1)*T(I)/2.
             RFONC= FONE(XC)
             BR=BR+H(I)*RFONC*SIN(XKITR*XC)
           enddo
           BR=BR*(XC2-XC1)
           sptsb0=sptsb0+br
           br=0.
           IPAS=IPAS+1
         ENDDO
       endif
       end
